refsection.programlisting:
initial_dir = PWD;
chdir (SIPDIR + 'images');

Img = imread('star.bmp');
xset('window',0);
xbasc()
imshow(Img,2);

[x,y] = follow(Img);  // get the parametric contour
t=1:size(x,'*');
xset('window',1)
xbasc()
subplot(121)
plot2d(t,x,2);
subplot(122)
plot2d(t,y,1);

xsm = gsm(x,15);    // gaussian-smooth the contour
ysm = gsm(y,15);
subplot(121)
plot2d(t,xsm,2);
subplot(122)
plot2d(t,ysm,1);

// builds an image from parametric contour:
Img2=unfollow(xsm,ysm,size(Img));
xset('window',0);
xbasc()
imshow(Img2,2);

chdir(initial_dir);
