refsection.programlisting:
rgb = imread(SIPDIR + 'images/tru.jpg'):
xbasc();
imshow(rgb);

hsv = rgb2hsv(rgb);
xbasc();
imshow(hsv(:,:,3))  // intensity
imshow(hsv(:,:,1))  // hue (color component)

hsv(:,:,2) = 1      // saturate the image

rgb = hsv2rgb(hsv);
xbasc();
imshow(rgb);

// You may also convert a single triplet:

rgb2hsv([1 1 0])
