refsection.programlisting:
initial_dir = PWD;
chdir (SIPDIR + 'images');

a=gray_imread('r.gif');
a=im2bw(a, 0.5);
[x,y]=follow(a);

npts = size(x,'*');
tgt=tangent(x,y, 1);

// Show the results
fig;
clf;
plot(x,y);
set(gca(), "isoview","on");
set(gca(), "auto_clear","off");

// todo use buffering
for i=1:size(x,'*')
  champ(x(i),y(i),tgt(i,1),tgt(i,2),0.5);
end

chdir(initial_dir);
