MAN.EXAMPLE:
initial_dir = PWD;
chdir (SIPDIR + 'images');

rgb = imread('tru.jpg');    // RGB colorspace 0-1
imshow(rgb);
yiq = rgb2ntsc(rgb);        // YIQ colorspace 0-1
yiq(:,:,2) = yiq(:,:,2) /4; // Decrease chromaticity
yiq(:,:,3) = yiq(:,:,3) /4;
rgb = ntsc2rgb(yiq);
imshow(rgb);

chdir(initial_dir);
